    public static Result map2() {
        String textText = "Pani Ala Kowalska, Sebastian Nowak i Zdzisław Nowacki mieszkają we Wrocławiu.";
        
        ObjectRecognize or = new ObjectRecognize();
        Text result = null;
        try {
            result = or.parseText(textText);
        } catch (IOException ex) {
            java.util.logging.Logger.getLogger(Application.class.getName()).log(Level.SEVERE, null, ex);
            return Results.internalServerError(ex.toString());
        }
        
        return ok(home.render(result.toStringTextObjects()));
    }
    
